select u.id as 'id', u.icon as 'icon', d.name as 'name', d.cost as 'cost', d.star_lv as 'starlv', (select GROUP_CONCAT(content) from sys_doctor_label where del_flag='0' and type='3' and id in (select labelid from sys_doctor_label_item where doctorid=u.id and del_flag='0')) as 'label', (select count(*) from sys_consultation_order where doctorid=u.id and orderstate='7' and del_flag='0') as 'consultation_times', (select sum(total) from sys_doctor_gift_order where doctorid=u.id and del_flag='0' and orderstate='2') as 'gifts' from sys_user_info u left join sys_doctor_info d on u.id = d.doctorid WHERE doctorid IN ( SELECT doctorid FROM ( SELECT doctorid, COUNT( doctorid ) AS 'count' FROM sys_doctor_label_item WHERE labelid IN
#{item}
and del_flag = '0' GROUP BY doctorid ) AS t
) and u.del_flag = '0' and d.del_flag = '0' limit #{pageModel.start}, #{pageModel.end}

select count(*) from sys_user_info u left join sys_doctor_info d on u.id = d.doctorid WHERE doctorid IN ( SELECT doctorid FROM ( SELECT doctorid, COUNT( doctorid ) AS 'count' FROM sys_doctor_label_item WHERE labelid IN
#{item}
and del_flag = '0' GROUP BY doctorid ) AS t
) and u.del_flag = '0' and d.del_flag = '0'

select u.id as 'id', u.icon as 'icon', d.name as 'name', d.cost as 'cost', d.star_lv as 'starlv', d.own_flag as 'ownFlag', (select GROUP_CONCAT(content) from sys_doctor_label where del_flag='0' and type='3' and id in (select labelid from sys_doctor_label_item where doctorid=u.id and del_flag='0')) as 'label', ((select count(*) from sys_consultation_order where doctorid=u.id and orderstate='7' and del_flag='0' )+ first_count ) as 'consultation_times', (select sum(total) from sys_doctor_gift_order where doctorid=u.id and del_flag='0' and orderstate='2') as 'gifts' from sys_user_info u left join sys_doctor_info d on u.id = d.doctorid where u.del_flag = '0' and d.del_flag = '0'
and d.cityid=#{cityid}
order by consultation_times ${hot}
order by gifts ${recommend}
limit #{pageModel.start}, #{pageModel.end}

select u.id as 'id', u.icon as 'icon', d.name as 'name', d.cost as 'cost', d.star_lv as 'starlv', d.come_cost as 'comecost', d.come_flag as 'comeflag', d.register_price as 'registerprice', d.own_flag as 'ownFlag', (select GROUP_CONCAT(content) from sys_doctor_label where del_flag='0' and type='3' and id in (select labelid from sys_doctor_label_item where doctorid=u.id and del_flag='0')) as 'label', ((select count(*) from sys_consultation_order where doctorid=u.id and orderstate='7' and del_flag='0' )+ first_count ) as 'consultation_times', (select sum(total) from sys_doctor_gift_order where doctorid=u.id and del_flag='0' and orderstate='2') as 'gifts' from sys_user_info u left join sys_doctor_info d on u.id = d.doctorid where u.del_flag = '0' and d.del_flag = '0'
and d.cityid=#{cityid}
and d.own_flag=#{ownFlag}
order by consultation_times ${hot}
order by gifts ${recommend}
limit #{pageModel.start}, #{pageModel.end}

select count(*) from sys_user_info u left join sys_doctor_info d on u.id = d.doctorid where u.del_flag = '0' and d.del_flag = '0'
and d.cityid=#{cityid}
and d.own_flag=#{ownFlag}

select u.nike_name as 'uname', DATE_FORMAT(go.update_date,"%Y-%m-%d %T") as 'time', go.amount as 'amount', '锦旗' as 'from' from sys_doctor_gift_order go left join sys_user_info u on u.id = go.userid where go.del_flag = '0' and go.orderstate = '2' and go.doctorid=#{id}
union all
select u.nike_name as 'uname', DATE_FORMAT(o.update_date,"%Y-%m-%d %T") as 'time', o.amount as 'amount', '问诊' as 'from' from sys_consultation_order o left join sys_user_info u on u.id = o.create_by where o.del_flag = '0' and o.orderstate = '7' and o.doctorid=#{id}
limit #{pageModel.start}, #{pageModel.end}

select sum(C) from ( select count(*) C from sys_doctor_gift_order go left join sys_user_info u on u.id = go.userid where go.del_flag = '0' and go.orderstate = '2' and go.doctorid=#{id}
union all
select count(*) C from sys_consultation_order o left join sys_user_info u on u.id = o.create_by where o.del_flag = '0' and o.orderstate = '7' and o.doctorid=#{id} ) T

select IFNULL(sum(S) , 0.00) from ( select sum(go.amount) S from sys_doctor_gift_order go left join sys_user_info u on u.id = go.userid where go.del_flag = '0' and go.orderstate = '2' and go.doctorid=#{id}
union all
select sum(o.amount) S from sys_consultation_order o left join sys_user_info u on u.id = o.create_by where o.del_flag = '0' and o.orderstate = '7' and o.doctorid=#{id} ) T
