mapper.select:
	select * from sys_article_info where 1=1 and del_flag='0'

  		and title like concat('%',#{title},'%')


  		and type = #{type}

  	limit #{pageModel.start}, #{pageModel.end}

	select count(*) from sys_article_info where 1=1 and del_flag='0'

  		and title like concat('%',#{title},'%')


  		and type = #{type}
