mapper.select:
select u.id as 'id', d.name as 'name', d.sex as 'sex', DATE_FORMAT(d.age,"%Y-%m-%d %T") as 'age', u.mobile as 'telephone', d.idcard as 'idcard', d.technical as 'technical', d.classify as 'classify', u.isauthentication as 'isauthentication', u.nike_name as 'nikeName' from sys_user_info u left join sys_doctor_info d on d.doctorid = u.id where u.del_flag = '0' and u.type = '2'
and d.name like concat('%', #{name}, '%')
and u.mobile like concat('%', #{telephone}, '%')
and d.idcard like concat('%', #{idcard}, '%')
and d.age = #{age}
and d.sex = #{sex}
and d.technical = #{technical}
and d.classify = #{classify}
and u.isauthentication = #{isauthentication}
${orderBy}

select count(*) from sys_user_info u left join sys_doctor_info d on d.doctorid = u.id where u.del_flag = '0' and u.type = '2'
and d.name like concat('%', #{name}, '%')
and u.mobile like concat('%', #{telephone}, '%')
and d.idcard like concat('%', #{idcard}, '%')
and d.age = #{age}
and d.sex = #{sex}
and d.technical = #{technical}
and d.classify = #{classify}
and u.isauthentication = #{isauthentication}
